  };

  const nextSlide = () => {
    sliderRef.slickNext();
  };
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        centerMode: true,
        centerPadding: '0px',
          afterChange: (current) => setCurrentSlide(current),
    };
    const renderImages = () =>
        images.map((image, index) => <CarouseItem key={index} image={image} />);
  return (
    <div className='flex items-center justify-center mb-8 my-8'>
          <div className='w-full my-8'>
              < Slider {...settings}> 
              {renderImages()}
              </Slider>


              
      </div>
    </div>
  );
}

export default Carousel;
