import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
// import { tours } from '../data'
import CarouseItem from './components/CarouseItem';
import tour1 from './images/tour-1.jpeg';
import tour2 from './images/tour-2.jpeg';
import tour3 from './images/tour-3.jpeg';
import tour4 from './images/tour-4.jpeg';
import tour5 from './images/tour-4.jpeg';
import tour6 from './images/tour-4.jpeg';
export const images = [tour1, tour2, tour3, tour4,tour5,tour6]
const Carousel = () => {
    const [currentSlide, setCurrentSlide] = React.useState(0);
 const prevSlide = () => {
    sliderRef.slickPrev();
  };

  const nextSlide = () => {
    sliderRef.slickNext();
    };
      let sliderRef;
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        centerMode: true,
        centerPadding: '0px',
          afterChange: (current) => setCurrentSlide(current),
    };
    const renderImages = () =>
        images.map((image, index) => <CarouseItem key={index} image={image} />);
  return (
    <div className='flex items-center justify-center mb-8 my-8'>
          <div className='w-full my-8'>
              < Slider  ref={(c) => (sliderRef = c)}{...settings}> 
              {renderImages()}
              </Slider>
<button onClick={event => { { prevSlide; } }}>Previous Slide</button>
      <button onClick={event => { { nextSlide; } }}>Next Slide</button>
      <p>Current Slide: {currentSlide}</p>

              
      </div>
    </div>
  );
}

export default Carousel;
